///Handling route based on bottom click actions
String getCurrentRoute(BottomBarEnum type) { switch (type) {case BottomBarEnum.Shop: return AppRoutes.homepageTwoPage; case BottomBarEnum.Category: return AppRoutes.productCategoryTwoPage; case BottomBarEnum.Cart: return "/"; case BottomBarEnum.Account: return AppRoutes.accountsOnePage; default: return "/";} } 
///Handling page based on route
Widget getCurrentPage(String currentRoute) { switch (currentRoute) {case AppRoutes.homepageTwoPage: return HomepageTwoPage(); case AppRoutes.productCategoryTwoPage: return ProductCategoryTwoPage(); case AppRoutes.accountsOnePage: return AccountsOnePage(); default: return DefaultWidget();} } 
